pythonClass:
class Calculator:
        def add(self, a, b):
            return a + b

        def subtract(self, a, b):
            return a - b

class Person:
        def __init__(self, name, age):
            self.name = name
            self.age = age

        def greet(self):
            return f"Hello, my name is {self.name} and I am {self.age} years old."

pythonTest:
def test_person():
        p = Person("Alice", 30)
        assert p.greet() == "Hello, my name is Alice and I am 30 years old."

